// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

contract  DataValidation {  // can''t be a library because it ocntains modifiers 

    modifier throwIfAddressIsInvalid(address _value) {
        if (_value == address(0)) revert("Invalid_ADDRESS");
        _;
    }
    modifier throwIfIsEmptyString(string memory _value) {
        if (bytes(_value).length == 0) revert("EMPTY_STRING");
        _;
    }
    modifier throwIfEqualToZero(uint _value) {
        if (_value == 0) revert("EQUAL_TO_ZERO");
        _;
    }
    modifier throwIfIsEmptyBytes32(bytes32  _value) {
        if (_value == "") revert("EMPTY_BYTES_32");
        _;
    }

}
